import 'package:flutter/material.dart';

void main(){
  runApp(FunFactsApp());
}

class FunFactsApp extends StatelessWidget {
  @override
  Widget build(BuildContext context){
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      title: "Fun Facts About me",
      home: FunFactsHome(),
    );
  }
}

class FunFactsHome extends StatefulWidget{
  @override
  _FunFactsHomeState createState() => _FunFactsHomeState();
}

class _FunFactsHomeState extends State<FunFactsHome> {
  int _counter = 0;
  String _fact = "Tap the button to reaveal a fun fact!";

  final List<String> _facts = [
    "I love grey colors!",
    "I'm a lazy programmer",
    "Money makes me feel peaceful",
    "I dont wear glasses",
    "I am, what i am",
  ];

  void _showFact() {
    setState(() {
      _fact = _facts[_counter % _facts.length];
      _counter++;
    });
  }

  @override 
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text("Fun Facts"), backgroundColor: Colors.teal,),
      body: Center(
        child: Padding(
          padding: EdgeInsets.all(16),
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              CircleAvatar(
                radius: 60,
                backgroundImage: NetworkImage('data:image/jpeg;base64,'),
              ),
              SizedBox(height: 20),
              Text(
                "Hi, I'm Jadu",
                style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
              ),
              SizedBox(height: 20),
              Text(
                _fact,
                style: TextStyle(fontSize: 18),
                textAlign: TextAlign.center,
              ),
              SizedBox(height: 20),
              ElevatedButton(
                onPressed: _showFact,
                child: Text("Show Fun Fact"),
              )
            ]
          ),
        ),
      ),
    );
  }
}